--[[
  Validate if group has reached max size.
]]

local function hasGroupReachedMaxSize(group, prefixKey)
  if group ~= nil then
    local groupId = group['id']
    
    local maxGroupSize = group['maxSize'] or 0
    if maxGroupSize > 0 then
      -- Check if group is already at max capacity
      local groupCount = getGroupCount(groupId, prefixKey)
      if groupCount >= maxGroupSize then
        -- Return error code for group at max capacity
        return true
      end
    end
  end
  return false
end
