import { SandboxedJob } from 'bullmq';
import { JobsProOptions } from './jobs-pro-options';
/**
 * A job that is being processed in a sandbox.
 *
 * @see {@link https://docs.bullmq.io/bullmq-pro/groups/sandboxes-for-groups}
 */
export interface SandboxedJobPro<T = any, R = any> extends Omit<SandboxedJob, 'data' | 'opts' | 'returnValue'> {
    data: T;
    opts: JobsProOptions;
    returnValue: R;
    /**
     * The group id of the job.
     */
    gid?: string;
}
