import { WorkerOptions } from 'bullmq';
export interface JobTtlMap {
    [jobName: string]: number;
}
export interface WorkerProOptions extends WorkerOptions {
    ttl?: number | JobTtlMap;
    group?: {
        limit?: {
            max: number;
            duration: number;
        };
        /**
         * Maximum number of simultaneous jobs that the workers can handle within a specific group.
         * For instance, setting this value to 1 ensures that no more than one job from the same
         * group is processed at any given time. If this limit is not defined, there will be no
         * restriction on the number of concurrent jobs.
         *
         * @see {@link https://docs.bullmq.io/bullmq-pro/groups/concurrency}
         */
        concurrency?: number;
    };
    batch?: {
        /**
         * Amount of jobs that can be fetched at once as a batch.
         * @see {@link https://docs.bullmq.io/bullmq-pro/batches}
         * @default 1
         */
        size?: number;
    };
}
